import React, { useState, useContext, useEffect } from 'react';
import { AlertContext } from '../../contexts/alert/AlertContext';
import { AuthContext } from '../../contexts/auth/AuthContext';

const Register = (props) => {
  const { setAlert } = useContext(AlertContext);
  const { register, error, clearErrors, isAuthenticated } = useContext(
    AuthContext
  );

  useEffect(() => {
    if (isAuthenticated) {
      props.history.push('/');
      setAlert(`Seja bem-vindo(a), ${name}`, 'info');
    }
    if (error === 'User already exists') {
      setAlert('Usuário já cadastrado', 'danger');
      clearErrors();
    }
  }, [error, isAuthenticated, props.history]);

  const [user, setUser] = useState({
    name: '',
    email: '',
    password: '',
    password2: '',
  });
  const onChange = (e) => setUser({ ...user, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
    if (name === '' || email === '' || password === '' || password2 === '') {
      setAlert('Preencha todos os campos', 'danger');
    } else if (password !== password2) {
      setAlert('As senhas não coincidem ', 'danger');
    } else {
      register({
        name,
        email,
        password,
      });
    }
  };

  const { name, email, password, password2 } = user;
  return (
    <section className='auth'>
      <h2>Cadastre-se</h2>
      <form onSubmit={onSubmit}>
        <input
          type='text'
          name='name'
          placeholder='Seu nome...'
          value={name}
          onChange={onChange}
        />
        <input
          type='email'
          name='email'
          value={email}
          placeholder='Seu e-mail...'
          onChange={onChange}
        />
        <input
          type='password'
          name='password'
          placeholder='Crie uma senha...'
          value={password}
          onChange={onChange}
          minLength='6'
        />
        <input
          type='password'
          name='password2'
          placeholder='Confirme a senha...'
          value={password2}
          onChange={onChange}
          minLength='6'
        />
        <button type='submit' className='btn register-button'>
          Cadastre-se
        </button>
      </form>
    </section>
  );
};

export default Register;
