import React, { useState, useContext, useEffect } from 'react';
import { AlertContext } from '../../contexts/alert/AlertContext';
import { AuthContext } from '../../contexts/auth/AuthContext';

const Login = (props) => {
  const { setAlert } = useContext(AlertContext);
  const { login, error, clearErrors, isAuthenticated } = useContext(
    AuthContext
  );

  useEffect(() => {
    if (isAuthenticated) {
      props.history.push('/');
      // setAlert(`Seja bem-vindo(a) de volta, ${name}`, 'info');
    }
    if (error === 'Invalid Credentials') {
      setAlert('Credenciais inválidas', 'danger');
      clearErrors();
    }
  }, [error, isAuthenticated, props.history]);

  const [user, setUser] = useState({
    email: '',
    password: '',
  });
  const { email, password } = user;
  const onChange = (e) => setUser({ ...user, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
    if (email === '' || password === '') {
      setAlert('Preencha todos os campos', 'danger');
    } else {
      login({
        email,
        password,
      });
    }
  };

  return (
    <section className='auth'>
      <h2>Entre com sua conta</h2>
      <form onSubmit={onSubmit}>
        <input
          type='email'
          name='email'
          placeholder='Seu e-mail...'
          value={email}
          onChange={onChange}
        />
        <input
          type='password'
          name='password'
          placeholder='Sua senha...'
          value={password}
          onChange={onChange}
        />
        <button className='btn login-button'>Entrar</button>
      </form>
    </section>
  );
};

export default Login;
