import React, { createContext, useReducer } from 'react';
import uuid from 'uuid/v4';
import TaskReducer from './TaskReducer';
export const TaskContext = createContext();

const TaskContextProvider = (props) => {
  const initialState = {
    tasks: [
      { id: 1, title: 'Limpar a casa', priority: 'média', done: true },
      { id: 2, title: 'Encontrar o tom', priority: 'alta', done: false },
      { id: 3, title: 'Pagar a luz', priority: 'baixa', done: true },
      { id: 4, title: 'fazer o café', priority: 'média', done: false },
    ],
    filtered: null,
    current: null,
  };

  const [state, dispatch] = useReducer(TaskReducer, initialState, () => {
    const localData = localStorage.getItem('inialState');
    return localData ? JSON.parse(localData) : [];
  });

  useEffect(() => {
    localStorage.setItem(initialState, JSON.stringify(initialState));
  }, [initialState]);

  /*********** **** FUNCTIONS  ***************/

  /********  Atenção   ******* */
  /*  payload é o valor/dado  */

  const addTask = (task) => {
    task.id = uuid();
    dispatch({
      type: 'ADD_TASK',
      payload: task,
    });
  };

  const toggleDone = (task) => {
    dispatch({ type: 'TOGGLE_DONE', payload: task });
  };

  const removeTask = (id) => {
    dispatch({ type: 'REMOVE_TASK', payload: id });
  };

  const clearList = () => {
    dispatch({ type: 'CLEAR_LIST' });
  };

  /*********** **** FUNCTIONS  ***************/

  return (
    <TaskContext.Provider
      value={{
        tasks: state.tasks,
        toggleDone,
        removeTask,
        clearList,
        addTask,
        dispatch,
        // filtered,
        // current,
      }}
    >
      {props.children}
    </TaskContext.Provider>
  );
};
export default TaskContextProvider;
