import React, { useContext, useEfect, Fragment } from 'react';
import { Link } from 'react-router-dom';
import { FaSignOutAlt } from 'react-icons/fa';
import { AuthContext } from '../../contexts/auth/AuthContext';

const Header = () => {
  const { isAuthenticated, logout, user, loadUser } = useContext(AuthContext);

  const onLogout = () => {
    logout();
  };
  const authLinks = (
    <Fragment>
      <li>Ol√°, {user && user.name}</li>
      <li>
        <a onClick={onLogout} href='/entrar'>
          <FaSignOutAlt /> {''}Sair
        </a>
      </li>
    </Fragment>
  );

  const guestLinks = (
    <Fragment>
      <li>
        <Link to='/cadastro'>Cadastrar-se</Link>
      </li>
      <li>
        <Link to='/entrar'>Entrar</Link>
      </li>
    </Fragment>
  );

  return (
    <nav>
      <div className='container flex-column'>
        <ul className='container flex-row'>
          <div>{isAuthenticated ? authLinks : guestLinks}</div>
        </ul>
        <h1 className='title'>Task List</h1>
      </div>
    </nav>
  );
};
export default Header;
